USE hike;

# -------------------------------------------------------------------START DURATION QUERIES------------------------------------------
# What are the top 10 shortest duration easy trails?
DROP PROCEDURE IF EXISTS getDurationTrails_ShortE; 
DELIMITER $$ 
CREATE PROCEDURE getDurationTrails_ShortE() BEGIN SELECT td.trailDurationMin, t.trailName, t.trailState, t.trailRegion, ti.trailDifficulty FROM trails t, trailDetails td, trailInfo ti WHERE t.trailID = td.trailID AND t.trailID = ti.trailID AND ti.trailDifficulty = 'Easy' ORDER BY td.trailDurationMin ASC LIMIT 10; 
END$$ 
DELIMITER ;
CALL getDurationTrails_ShortE();

# What are the top 10 longest duration easy trails?
DROP PROCEDURE IF EXISTS getDurationTrails_LongE; 
DELIMITER $$ 
CREATE PROCEDURE getDurationTrails_LongE() BEGIN SELECT td.trailDurationMin, t.trailName, t.trailState, t.trailRegion, ti.trailDifficulty FROM trails t, trailDetails td, trailInfo ti WHERE t.trailID = td.trailID AND t.trailID = ti.trailID AND ti.trailDifficulty = 'Easy' ORDER BY td.trailDurationMin DESC LIMIT 10; 
END$$ 
DELIMITER ;
CALL getDurationTrails_LongE();

