# ------------------------------------------START COMMON REGION QUERIES------------------------------------------
# What are the most common regions?
SELECT trailRegion, trailState, COUNT(trailRegion) commonRegionCount FROM trails GROUP BY trailRegion ORDER BY commonRegionCount DESC LIMIT 35;
# ------------------------------------------END COMMON REGION QUERIES------------------------------------------
# ------------------------------------------START AVERAGE QUERIES------------------------------------------
# What is the average ELEVATION of easy, intermediate, and advanced trails?
SELECT AVG(td.trailElevationFt) AS AverageElevation, ti.trailDifficulty FROM trailDetails td, trailInfo ti WHERE td.trailID = ti.trailID AND ti.trailDifficulty = 'Easy' UNION ALL
SELECT AVG(td.trailElevationFt) AS AverageElevation, ti.trailDifficulty FROM trailDetails td, trailInfo ti WHERE td.trailID = ti.trailID AND ti.trailDifficulty = 'Intermediate' UNION ALL
SELECT AVG(td.trailElevationFt) AS AverageElevation, ti.trailDifficulty FROM trailDetails td, trailInfo ti WHERE td.trailID = ti.trailID AND ti.trailDifficulty = 'Advanced';
    
# What is the average LENGTH of easy, intermediate, and advanced trails?
SELECT AVG(td.trailLengthMi) AS AverageLengthInMiles, ti.trailDifficulty FROM trailDetails td, trailInfo ti WHERE td.trailID = ti.trailID AND ti.trailDifficulty = 'Easy' UNION ALL
SELECT AVG(td.trailLengthMi) AS AverageLengthInMiles, ti.trailDifficulty FROM trailDetails td, trailInfo ti WHERE td.trailID = ti.trailID AND ti.trailDifficulty = 'Intermediate'UNION ALL 
SELECT AVG(td.trailLengthMi) AS AverageLengthInMiles, ti.trailDifficulty FROM trailDetails td, trailInfo ti WHERE td.trailID = ti.trailID AND ti.trailDifficulty = 'Advanced';
    
# What is the average RATING of easy, intermediate, and advanced trails?
SELECT AVG(ti.trailRating) AS AverageRatingOutOf5, ti.trailDifficulty FROM trailDetails td, trailInfo ti WHERE td.trailID = ti.trailID AND ti.trailDifficulty = 'Easy' UNION ALL
SELECT AVG(ti.trailRating) AS AverageRatingOutOf5, ti.trailDifficulty FROM trailDetails td, trailInfo ti WHERE td.trailID = ti.trailID AND ti.trailDifficulty = 'Intermediate' UNION ALL
SELECT AVG(ti.trailRating) AS AverageRatingOutOf5, ti.trailDifficulty FROM trailDetails td, trailInfo ti WHERE td.trailID = ti.trailID AND ti.trailDifficulty = 'Advanced';
    
# What is the average DURATION of easy, intermediate, and advanced trails?
SELECT AVG(td.trailDurationMin) AS AverageDurationMin, ti.trailDifficulty FROM trailDetails td, trailInfo ti WHERE td.trailID = ti.trailID AND ti.trailDifficulty = 'Easy' UNION ALL
SELECT AVG(td.trailDurationMin) AS AverageDurationMin, ti.trailDifficulty FROM trailDetails td, trailInfo ti WHERE td.trailID = ti.trailID AND ti.trailDifficulty = 'Intermediate' UNION ALL
SELECT AVG(td.trailDurationMin) AS AverageDurationMin, ti.trailDifficulty FROM trailDetails td, trailInfo ti WHERE td.trailID = ti.trailID AND ti.trailDifficulty = 'Advanced';

