#---------------------------------------------------------DURATION---------------------------------------------------------
# What is the average duration of easy trails in every state?
SELECT ROUND(AVG(td.trailDurationMin), 4) AS AverageTrailDurationInMin, t.trailState, ti.trailDifficulty FROM trails t, trailInfo ti, trailDetails td WHERE t.trailID = ti.trailID AND td.trailID = t.trailID AND t.trailState = 'Alabama' AND ti.trailDifficulty = 'Easy' UNION ALL 
